import{_ as a,c as l,g as e,o as n}from"./chunks/framework.n-91xA-b.js";const u=JSON.parse('{"title":"关于人形成世界观的一些想法","description":"","frontmatter":{"title":"关于人形成世界观的一些想法","date":"2015-01-26T01:51:27.416Z","length":"583字","time":"2分钟","aside":false,"hash":-1779101993},"headers":[],"relativePath":"article/关于人形成世界观的一些想法.md","filePath":"article/关于人形成世界观的一些想法.md"}'),r={name:"article/关于人形成世界观的一些想法.md"};function _(s,t,o,p,i,c){return n(),l("div",null,t[0]||(t[0]=[e("p",null,"唯物主义、主观唯心、客观唯心主义，究竟是什么促使人形成这些不同的世界观呢？我就这个问题发表一下我自己的看法。",-1),e("p",null,"唯物主义的观点应该是很朴素的，至少是实践的观点。因为从原始人类进化而来，人获取最基本的生存资料的方式不可能脱离实践。你若说服一个底层劳动者（物质资料的直接生产者）可以不通过任何劳动，甚至不偷不抢就能获得生存，是全然不可能的。唯物主义（或至少说唯物思想的萌芽）一定是深深植根于直接生产者的思想中的。",-1),e("p",null,"客观唯心主义的产生也很好理解。由于生产力的低下，人的生存、社会发展的压力巨大，加之以统治者（奴隶主或封建地主）的洗脑，对自然界或是君主就会产生一种盲目的崇拜，在观念幻化为这样或那样的神灵，从而就有了客观唯心的萌芽。",-1),e("p",null,"然而主观唯心的产生就颇耐人寻味了。在较低生产力的情况和较少知识储备的情况下，要形成以自我为中心的观点，是需要多么大的勇气啊！因而不难发现，主观唯心主义哲学家大致在文艺复兴后出现。",-1),e("p",null,"具有主观唯心主义萌发的动因应当是具有历史色彩的。文艺复兴，注重人的自我解放与冲破宗教禁锢，人的思想中心便从神（客观唯心）转向了人（主观唯心），随着进一步的自我觉醒以及资产阶级革命，推翻利用封建宗教工具控制人民的封建君主统治需要越来越迫切，加之人在改造自然和自然科学领域的不断胜利，人定胜天、人的意志被空前肯定，主观唯心主义最终形成。",-1),e("p",null,"主观唯心主义的巅峰应该就属于唯意志论了。但我还了解不多。关于这种强力意志的思想形成，我猜测应该同个人经历与心理状态有关，不然尼采怎么会疯了呢？",-1)]))}const m=a(r,[["render",_]]);export{u as __pageData,m as default};
