import{_ as s,c as l,ao as p,g as e,a,o}from"./chunks/framework.n-91xA-b.js";const f=JSON.parse('{"title":"我的写作观","description":"","frontmatter":{"title":"我的写作观","date":"2016-01-29T13:07:22.291Z","length":"2752字","time":"10分钟","aside":false,"hash":-439501583},"headers":[],"relativePath":"article/我的写作观.md","filePath":"article/我的写作观.md"}'),i={name:"article/我的写作观.md"},r={class:"MathJax",jax:"SVG",style:{direction:"ltr",position:"relative"}},n={style:{overflow:"visible","min-height":"1px","min-width":"1px","vertical-align":"-0.025ex"},xmlns:"http://www.w3.org/2000/svg",width:"1.29ex",height:"1ex",role:"img",focusable:"false",viewBox:"0 -431 570 442","aria-hidden":"true"};function d(m,t,u,x,h,T){return o(),l("div",null,[t[4]||(t[4]=p("",20)),e("p",null,[t[2]||(t[2]=a("我写作的内容，正如前所言，最初仅为记录想法、进而伴随着抒发感情，即无非理性与感性二种。理性，我曾为他着迷过好一阵子，连久未更改的qq昵称都改成“理性")),e("mjx-container",r,[(o(),l("svg",n,t[0]||(t[0]=[e("g",{stroke:"currentColor",fill:"currentColor","stroke-width":"0",transform:"scale(1,-1)"},[e("g",{"data-mml-node":"math"},[e("g",{"data-mml-node":"mi"},[e("path",{"data-c":"1D70B",d:"M132 -11Q98 -11 98 22V33L111 61Q186 219 220 334L228 358H196Q158 358 142 355T103 336Q92 329 81 318T62 297T53 285Q51 284 38 284Q19 284 19 294Q19 300 38 329T93 391T164 429Q171 431 389 431Q549 431 553 430Q573 423 573 402Q573 371 541 360Q535 358 472 358H408L405 341Q393 269 393 222Q393 170 402 129T421 65T431 37Q431 20 417 5T381 -10Q370 -10 363 -7T347 17T331 77Q330 86 330 121Q330 170 339 226T357 318T367 358H269L268 354Q268 351 249 275T206 114T175 17Q164 -11 132 -11Z",style:{"stroke-width":"3"}})])])],-1)]))),t[1]||(t[1]=e("mjx-assistive-mml",{unselectable:"on",display:"inline",style:{top:"0px",left:"0px",clip:"rect(1px, 1px, 1px, 1px)","-webkit-touch-callout":"none","-webkit-user-select":"none","-khtml-user-select":"none","-moz-user-select":"none","-ms-user-select":"none","user-select":"none",position:"absolute",padding:"1px 0px 0px 0px",border:"0px",display:"block",width:"auto",overflow:"hidden"}},[e("math",{xmlns:"http://www.w3.org/1998/Math/MathML"},[e("mi",null,"π")])],-1))]),t[3]||(t[3]=a("”。正因为那阵于我痴迷于数理，脑中里满是拉普拉斯式的狂妄与崇拜。但很快我发现，脱离感性空谈理性不切实际，反之亦然。纯粹的理性像烈火般炙烤得我口干舌燥，必须加以感性的水滋润。"))]),t[5]||(t[5]=e("p",null,"随着学业加重，我过剩的精力不得不收缩了，因而思辨渐渐少了。同样也因为我的世界观日臻成熟，很难有某些巨大的变动。抒情与排解苦闷多了起来。当然也不必苛求日日冒出什么新思想，像个捐急的学者，那样反而会心太累，如同只有烈日而无甘霖。生活是需要调剂的，要有适度的休息，用抒情来疏导郁结的思绪。",-1)),t[6]||(t[6]=e("p",null,"谈到形式，其实就是字词句式的选择运用。我写作的形式的核心准则是随由心而发，随心而动，要选择最恰当的词，如果没有可以生造，这里也有声音的考虑，听着要顺耳，富有韵律，因为我在欣赏她的时候，总要默读赋之以声.（头脑中那个念书的小人）我甚至生造过意思，因我当时不愿在一个词上费心思索以致断了文思，而是暂时存而不论，以后再改或索性不改。注意，形式都是为了内容而服务的，我们总要为内容赋形式并透过形式理解内容。对我而言，这形式的选取主要是直觉的。",-1)),t[7]||(t[7]=e("p",null,"其实所有文学作品都深深地体现了这一点。譬如语气，它作为内容的一部分，在纸上无法通过叙述者直接发声来体现，怎么办？于是各种各样的虚词被造了出来，表反问，表感叹这种感情的好理解，但有些纯粹是为了凑音节、取消句子独立性的字词让人觉着无用多余了。果真如此吗？你可以试着去掉。文言文去了这些词会让你感觉嚼风干了的面——丝毫感觉不到软来。文言文妙在短而精，正是短而精，更需要比白话文多得多的虚词来填充，好让你在虚词上充分的停歇，以分析领悟那字字珠玑的实词意味，而非窘于应付连珠炮似的堆砌。同样，白话文亦是如此，就拿“的”字举例，这大概最常用，试着在文段中去掉它，你会难过的舌头发酥，节奏失调，这就好比是一唱三叹，好比心脏搏动的大半时间在休息。",-1)),t[8]||(t[8]=e("div",{class:"info custom-block"},[e("p",{class:"custom-block-title"},"后记"),e("p",null,"如今我更倾向于减少“的”的使用次数，这样文气更贯通些。当然，所谓“论文”如今整理时是不忍呈现给大家的，它们的意义我在《论知识的再发现》谈过。")],-1))])}const Q=s(i,[["render",d]]);export{f as __pageData,Q as default};
