import{_ as a,c as l,g as e,o as n}from"./chunks/framework.n-91xA-b.js";const m=JSON.parse('{"title":"收回那过远的目光","description":"","frontmatter":{"title":"收回那过远的目光","date":"2015-01-04T01:41:12.094Z","length":"456字","time":"2分钟","aside":false,"hash":486143475},"headers":[],"relativePath":"article/收回那过远的目光.md","filePath":"article/收回那过远的目光.md"}'),r={name:"article/收回那过远的目光.md"};function s(o,t,_,i,p,c){return n(),l("div",null,t[0]||(t[0]=[e("p",null,"人们都称赞目光长远，却也未必知道目光长远也有弊病。也许为了保护这褒词的纯洁性，应另辟新词，总之，我们要谈的是那过远的目光。",-1),e("p",null,"以登山为例，若你去爬那接天的山峰，直往上望，怕会吓破了胆，种种不自信一袭而来，怎么爬得上去？自己有体力吗？显然，当你还未迈出第一步的时候，你已经退缩了，这遍是望的过远的负面，类似的是一场马拉松，把目标定在终点的人，不少都在半路放弃了，询问那冠军则直接说：“我把40多公里的赛程分开成许多小路程，当我跑完一个五公里的时候，我只想着下一个五公里，我把目标定小一点，不知不觉就完成个了大目标。”",-1),e("p",null,"确实，当一个目标过于庞大时，不如分其为一个个小目标，逐一实现，更容易成功。",-1),e("p",null,"目光过远还体现在另一方面。我曾抱着这样的态度问，“反正世界、人类终归要毁灭，我这么努力，人类如此奋斗，所有成果最终化为泡影，有何意义？”因而“不如不做”，这种消极的想法在我的脑海中萦绕了许久，直至最近，在父母帮助下，才逐渐克服，这确实是目光过远或曰思考过多，对于这问题有好多解法：谁说一定会灭亡？我完全可以否定这观点，退一步说，就算会灭亡，不如享受这过程，灾难可以抹去实在的成品，但就算它弥漫悲痛，也不要忘了那沉在心底的美好记忆。",-1),e("p",null,"别望得太远，你属于现在。",-1)]))}const u=a(r,[["render",s]]);export{m as __pageData,u as default};
