import{_ as l,c as a,g as e,o as s}from"./chunks/framework.n-91xA-b.js";const _=JSON.parse('{"title":"无穷悖论","description":"","frontmatter":{"title":"无穷悖论","date":"2015-08-01T08:52:07.261Z","length":"480字","time":"2分钟","aside":false,"hash":1157602389},"headers":[],"relativePath":"article/无穷悖论.md","filePath":"article/无穷悖论.md"}'),n={name:"article/无穷悖论.md"};function o(r,t,c,p,i,u){return s(),a("div",null,t[0]||(t[0]=[e("p",null,"和康德一样，我是一个不可知论者。我不否认我们认识世界的能力，我却否认我们完全认识、彻底认识的能力。",-1),e("p",null,"可知论者是否太自大些了呢？以我们有限的人类，可能逼近无限的自然呢？",-1),e("p",null,"我知道，你们坚持可知论是不让后世者消极，但也不能说谎啊。后世者迟早会经历一种消极的心态：反正诸事均将衰亡，活着有何意义？成也意义，败也意义。若要从这抑郁的心境中走出来，则必为当下的生活找一个意义。",-1),e("p",null,"当他们真从这段抑郁中走出来后，我想，他们也便有足够强的心智接受这个无力而无奈的现实，我们人的囿限，我们智力孤危的时刻，我们智慧终结的那一天。",-1),e("p",null,"口说无凭，我来证明。这是一个关于无穷的悖论。逻辑学有云：内涵愈少，外延愈多。宇宙是无穷的，即它的外延无穷，那么宇宙的内涵便为零。好比中国古代哲学中的无极，无中生有，由无到无穷。",-1),e("p",null,"而人，为了达到认识的功能，就必须特化出认识器官。认识器官是有内涵的，因而它就有局限了，它已失去了与无限比肩的资格。它只能认识它可认识的部分，对于其他，无能为力。",-1),e("p",null,"因而得出结论，除非不断压缩我们认识的内涵否则无法致一切。",-1),e("div",{class:"info custom-block"},[e("p",{class:"custom-block-title"},"后记"),e("p",null,"接上篇文章所言，有限性是分割世界，把对象从背景性秩序中凸显出来的强大能力。而无限性也只有可能通过对有限性的否定得到认知。在有限性之前，一切只是尚未区分的绝对同一的黑夜。")],-1)]))}const m=l(n,[["render",o]]);export{_ as __pageData,m as default};
