import{_ as a,c as l,g as e,o as n}from"./chunks/framework.n-91xA-b.js";const u=JSON.parse('{"title":"社交中的场现象","description":"","frontmatter":{"title":"社交中的场现象","date":"2014-12-21T01:37:25.686Z","length":"467字","time":"2分钟","aside":false,"hash":-625147627},"headers":[],"relativePath":"article/社交中的场现象.md","filePath":"article/社交中的场现象.md"}'),r={name:"article/社交中的场现象.md"};function s(o,t,p,i,_,c){return n(),l("div",null,t[0]||(t[0]=[e("p",null,"我现在来阐述一下所谓的“场”在社交中的奇妙体现每个人都有着不同的性格，而这些人的行为举止深受他们的性格影响，每个人的言谈中又流露各自的爱好，即释放出一些信息，旁人根据这些信息做着这样那样的衡量和比较，并作出个人对此人的评价，每个人的评价都是不同的，这取决于所释放的信息与人内心的标准。",-1),e("p",null,"这颇像物理学中“场”的概念，实物与实物之间的相互作用由场维系，不同的场产生不同的相互作用.一个实物会“判断”来自或发自另一个实物的场信息，并内视自己的性质，作出适当的反应，产生这样或那样的作用。",-1),e("p",null,"因此，对于一个人来说，他的言行举止不仅作为别人衡量评价、交友的依据，同样帮助此人本身筛选出了“目标”伙伴，因为性格相同的人往往接近，正所谓物以类聚，人以群分。",-1),e("p",null,"恰巧的是，物理中的场也会有类聚群分，相似相应的现象，只有具有相同性质的物，如带电球与带电球发生电磁相互作用。即便不带电的球，也是因为内部独电荷迁移而变成化而得以参与作用。",-1),e("p",null,"进一步解放思想，我仿佛看见“信息”与“场”的概念紧紧维系，每个人都激发出一个不可见但可感的“信息场”，或直接或间接地对外传送着信息，人就在庞大信息场。构成的信息网络上实时上传和下载信息，交换与传播信息。",-1),e("p",null,"这当是一篇有启发性意义的随笔。",-1)]))}const m=a(r,[["render",s]]);export{u as __pageData,m as default};
