import{_ as l,c as a,g as e,o as n}from"./chunks/framework.n-91xA-b.js";const u=JSON.parse('{"title":"科学领军人物培养计划","description":"","frontmatter":{"title":"科学领军人物培养计划","date":"2015-03-03T02:17:33.445Z","length":"487字","time":"2分钟","aside":false,"hash":-1624134894},"headers":[],"relativePath":"article/科学领军人物培养计划.md","filePath":"article/科学领军人物培养计划.md"}'),r={name:"article/科学领军人物培养计划.md"};function s(_,t,o,p,i,c){return n(),a("div",null,t[0]||(t[0]=[e("p",null,"毋庸置疑，当今科学的现状便是各科学部门高度分化，形成数目庞大的分支，并且各分支内容更为复杂化，一般科学家最多精通一门专业，在应对复杂综合问题时，需要多类型科学家参与，分工协作。",-1),e("p",null,"但在解决问题过程中，确定涉及学科并选拔相关研究人员的步骤最为重要。因而这就需要一位对各方面科学均有所涉猎的人物统筹规划以便洞悉问题所涉方面，这样的“全学型”人才就是所谓的科学领军人物。",-1),e("p",null,"随着科学不断发展，科学体系不断繁杂化，科学领军人物在自然状态下孕育产生便难以胜任，因而迫切需要采取系统化方法培养科学领军人物。",-1),e("p",null,"俗话说“千招会不如一招精”，但领军人物可以做到“千招精”，亦是所谓的“全学家”（我幼时的梦想）",-1),e("p",null,"首先要筛选 7 岁左右的天资聪颖的幼童成为苗子。向苗子按逻辑顺序逐步传授科学知识，这一步需要借助知识发展脉络图。绘制脉络图则需要参考相关科学的发展史，因为历史上的科学发展顺序，除少数极具前瞻性的天才创见（如对数），基本与人类逻辑顺序重合，由浅入深，由简到繁。",-1),e("p",null,"对苗子进行正确价值观引导与高效学习方法传授也不可忽视。激发其兴趣是根本方法。并应较早培养自学能力，要预见其自学速度甚至超过教师传授速度的情况。",-1),e("p",null,"“希尔伯特被认为是最后一个精通各个数学分支的天才。”我希望将有一天，此话作古，且并不局限于数学。",-1)]))}const m=l(r,[["render",s]]);export{u as __pageData,m as default};
