import{_ as l,c as a,g as e,o as n}from"./chunks/framework.CCxIPCIe.js";const _=JSON.parse('{"title":"论记忆","description":"","frontmatter":{"title":"论记忆","date":"2015-05-21T14:27:19.053Z","length":"481字","time":"2分钟","aside":false,"hash":-775509959},"headers":[],"relativePath":"article/论记忆.md","filePath":"article/论记忆.md"}'),r={name:"article/论记忆.md"};function s(o,t,p,i,c,d){return n(),a("div",null,t[0]||(t[0]=[e("p",null,"记忆是生存的要素。",-1),e("p",null,"劳动区分了人与其他动物，而劳动作为一项技能，离不开学习，学习就是记忆的过程。技能以记忆的形式贮存于头脑，使用时便重复这个记忆。",-1),e("p",null,"记忆是交流的基础。",-1),e("p",null,"交流的工具一语言，需要记忆。交谈的目的，过程和内容需要记忆。即便低等生命，通过本能反应交流，而本能也是一种记忆，一种先天记忆。",-1),e("p",null,"记忆是意识的前提。以观察世界、生活于世界，若不时刻记忆着局域内的时空，就无法形成时空及其派生概念。无法形成时空感觉，更无法形成自我意识。无法形成相继、记忆是 连续概念，生物体就会崩溃，无法适应自然。",-1),e("p",null,"记忆是社会秩序的根基。无论是法、道德、规矩，都依赖于人脑的记忆。如果你认为它隶属于载体，那么无人记起这载体，也无济于事。人类社会秩序要用时间规范，人类社会每一天的进程，都依赖于时间的记忆。没有时间的记忆，工作者就不会按时上工，社会就完蛋、",-1),e("p",null,"我有一个文科班的同学，大概因为记背任务繁重，向我抱怨脑子塞满，记不住知识点。我笑了，脑子怎么可能装满呢？我主张人脑无限论，即便构成人脑的物质有限。我们如何感知无限呢？就是永远没有穷尽。也许人脑记忆是有限的，但这个上限太大了，我们永远不会达到。对于一般的信息存储，我们很容易破坏它，但人脑的记忆是很难忘却的。因而我们人脑不同于一般的信息贮存工具，是无限容量的。",-1)]))}const m=l(r,[["render",s]]);export{_ as __pageData,m as default};
