import{_ as s,c as a,g as e,a as l,ap as r,o}from"./chunks/framework.CCxIPCIe.js";const u=JSON.parse('{"title":"谈教材","description":"","frontmatter":{"title":"谈教材","date":"2018-11-11T14:18:12.412Z","length":"1033字","time":"4分钟","aside":false,"hash":200920595},"headers":[],"relativePath":"article/谈教材.md","filePath":"article/谈教材.md"}'),n={name:"article/谈教材.md"},i={class:"MathJax",jax:"SVG",style:{direction:"ltr",position:"relative"}},T={style:{overflow:"visible","min-height":"1px","min-width":"1px","vertical-align":"-0.439ex"},xmlns:"http://www.w3.org/2000/svg",width:"6.79ex",height:"2.009ex",role:"img",focusable:"false",viewBox:"0 -694 3001.3 888","aria-hidden":"true"};function d(m,t,p,Q,h,x){return o(),a("div",null,[t[4]||(t[4]=e("p",null,"倘若衡量一国教育质量，投入的师资和产出的学生不足以成为依据，因为这些资源的调配难以均分，从而无法反映全域的教育水平，而平均水平则毫无意义。在我看来，教材是融入了一国的教育理念，又能轻易地为制度贯彻，是反映一国教育绝佳的镜子。",-1)),t[5]||(t[5]=e("p",null,"有所凭借才能有所发挥，倘若没有教材，老师便成了光杆司令，不但不同老师上课天差地别，老师自己也难面面俱到。教材是死的，教师是活的，平常我们总谈发挥人的灵活能动性，其实死的东西也要发挥它死的优点。譬如说不刊一词，教材能一字不差地记下所有东西，并且面面俱到，这就要求我们教材编写要无比细致臻于完美。照本宣科似乎是个贬义词，然而好的教材就配得上照本宣科，因为它不容更改。（正如 Dirac 的轶事那样）死的东西就应该是死的，变化应该交于老师来演绎，有规矩才成方圆。这就像剧本与演员的关系。",-1)),e("p",null,[t[2]||(t[2]=l("我读中国的泛函教材，饱受教材拙劣之苦。一本数学教科书，首先是把话说清楚，至于介绍的内容多少，难易与否都是次要的。所谓清楚易懂，自然是对具备基础的新生而言。从而清楚的具体要求是：符号统一。前提详尽、过程连贯、结论简洁、细节指明。都说中国好算，但就热衷铺陈演算、大写特写，符号成山，以抽象为荣——谈到前提结论，或是证明的大意，即凡是触及语言的表述，则大而化之，恨不得掩饰过去。我不知道这种习气是固有的还是从苏联那边学来的，总之凭我的阅读经验，英文教材里根本没有这种痕迹。即便从历史上看，那些不成熟的数学家也是用朴素的自然语言“敷衍”证明，而不会用成篇的符号喧宾夺主。依我看，国内这些著者对数学语言以及汉语误解很深。他们为了所谓地简洁，肆意地将逻辑符号")),e("mjx-container",i,[(o(),a("svg",T,t[0]||(t[0]=[r('<g stroke="currentColor" fill="currentColor" stroke-width="0" transform="scale(1,-1)"><g data-mml-node="math"><g data-mml-node="mi"><path data-c="2200" d="M0 673Q0 684 7 689T20 694Q32 694 38 680T82 567L126 451H430L473 566Q483 593 494 622T512 668T519 685Q524 694 538 694Q556 692 556 674Q556 670 426 329T293 -15Q288 -22 278 -22T263 -15Q260 -11 131 328T0 673ZM414 410Q414 411 278 411T142 410L278 55L414 410Z" style="stroke-width:3;"></path></g><g data-mml-node="mo" transform="translate(556,0)"><path data-c="2C" d="M78 35T78 60T94 103T137 121Q165 121 187 96T210 8Q210 -27 201 -60T180 -117T154 -158T130 -185T117 -194Q113 -194 104 -185T95 -172Q95 -168 106 -156T131 -126T157 -76T173 -3V9L172 8Q170 7 167 6T161 3T152 1T140 0Q113 0 96 17Z" style="stroke-width:3;"></path></g><g data-mml-node="mi" transform="translate(1000.7,0)"><path data-c="2203" d="M56 661T56 674T70 694H487Q497 686 500 679V15Q497 10 487 1L279 0H70Q56 7 56 20T70 40H460V327H84Q70 334 70 347T84 367H460V654H70Q56 661 56 674Z" style="stroke-width:3;"></path></g><g data-mml-node="mo" transform="translate(1556.7,0)"><path data-c="2C" d="M78 35T78 60T94 103T137 121Q165 121 187 96T210 8Q210 -27 201 -60T180 -117T154 -158T130 -185T117 -194Q113 -194 104 -185T95 -172Q95 -168 106 -156T131 -126T157 -76T173 -3V9L172 8Q170 7 167 6T161 3T152 1T140 0Q113 0 96 17Z" style="stroke-width:3;"></path></g><g data-mml-node="mo" transform="translate(2001.3,0)"><path data-c="21D2" d="M580 514Q580 525 596 525Q601 525 604 525T609 525T613 524T615 523T617 520T619 517T622 512Q659 438 720 381T831 300T927 263Q944 258 944 250T935 239T898 228T840 204Q696 134 622 -12Q618 -21 615 -22T600 -24Q580 -24 580 -17Q580 -13 585 0Q620 69 671 123L681 133H70Q56 140 56 153Q56 168 72 173H725L735 181Q774 211 852 250Q851 251 834 259T789 283T735 319L725 327H72Q56 332 56 347Q56 360 70 367H681L671 377Q638 412 609 458T580 514Z" style="stroke-width:3;"></path></g></g></g>',1)]))),t[1]||(t[1]=e("mjx-assistive-mml",{unselectable:"on",display:"inline",style:{top:"0px",left:"0px",clip:"rect(1px, 1px, 1px, 1px)","-webkit-touch-callout":"none","-webkit-user-select":"none","-khtml-user-select":"none","-moz-user-select":"none","-ms-user-select":"none","user-select":"none",position:"absolute",padding:"1px 0px 0px 0px",border:"0px",display:"block",width:"auto",overflow:"hidden"}},[e("math",{xmlns:"http://www.w3.org/1998/Math/MathML"},[e("mi",{mathvariant:"normal"},"∀"),e("mo",null,","),e("mi",{mathvariant:"normal"},"∃"),e("mo",null,","),e("mo",{stretchy:"false"},"⇒")])],-1))]),t[3]||(t[3]=l(" 和汉字（多为文言单字）掺杂一团，形成一坨违反直觉的秽物。现在的数学家使用经过一套特殊处理的自然语言作为数学语言，而非形式逻辑的那种符号语句，可以说符号只是辅助记号而不是目的。这些人是否考虑过这种晦涩的文本对初学者的伤害？汉字本身和数学符号外形迥异，人们需要慢慢习惯混合文本的不适，然而他们的做法，使得文字-符号比例不相上下，那还能读吗？"))]),t[6]||(t[6]=e("p",null,"此外轻视前提和结论的叙述。［我很想说，你学西方的东西却不效仿他们的态度和叙述，难道你觉得西方处理自己的造物还不如你么？因而很多人都觉得欧美教材平易近人，就像说话一样聊天一样，正是因为他们的教材浸透了讲清楚话这个理念］对此我建议模仿西方教材，不厌其烦地解释每一个涉及的概念；在每个符号首次出现时前面添上它所指的东西；把符号和文字分开；避免混合。",-1)),t[7]||(t[7]=e("p",null,"最令我失望的事是，我们所用的这本张恭庆编泛函分析，三十年来竟无一次更新，以为自己比宪法还牛。尽管如此竟还被广大高校奉为经典教材，重印至今，无一丝淘汰迹象。书本不更新换代是作者自甘堕落，三十年间熟视无睹，中国教育质量、学界治学态度，未来人才培养，由此可见一斑。",-1))])}const w=s(n,[["render",d]]);export{u as __pageData,w as default};
