import{_ as a,c as r,g as e,o as n}from"./chunks/framework.n-91xA-b.js";const m=JSON.parse('{"title":"一一五","description":"","frontmatter":{"title":"一一五","date":"2021-05-28T09:07:33.000Z","length":"875字","time":"3分钟","aside":false,"hash":1887485049},"headers":[],"relativePath":"idea/115.md","filePath":"idea/115.md"}'),l={name:"idea/115.md"};function s(o,t,i,p,d,c){return n(),r("div",null,t[0]||(t[0]=[e("p",null,[e("a",{href:"https://zhuanlan.zhihu.com/p/354916372",target:"_blank",rel:"noreferrer"},"网页链接")],-1),e("p",null,"与我个人观点基本一致的一篇文章。浅谈自己对这个问题的理解。马克思的唯物唯心划分，其实是对当时西方的思想界的分析批判。马克思是否对东方哲学有所了解，我不清楚。但是我对高中教材中关于唯心和唯物的某些判断一直心存疑窦。譬如从笛卡尔我思故我在这句话判断它是主观唯心主义，这显然是不对的。笛卡尔从思维的存在性反推出我的存在性，是因为他坚信没有存在就不会有思维，这相反是一个相当唯物的观点。（但这并不是说笛卡尔最终不是唯心主义，毕竟还能找到其他更可靠的论据。）而教材对中国古今许多哲学家都做了唯物唯心的种种划分，以至于在上完思想政治的那一瞬间，我竟以为我所学习的对这些例子的划分，是我已经彻底了解掌握他们后得出的结论，甚至比这更自负——幻生出一种曾经沧海难为水的错觉，认为除了马克思主义，其他都是浮云。（这本身是反辩证法的，因为赞同要从怀疑从来，反对要从熟悉中来）这种狂妄往后便一直未经反思，直到前两个月看到一本讲中国古代哲学的法国讨论班教材，我才发现我几乎都不认识（对先人再表羞愧），顿时觉得，高中教材从只言片语出发，就为各个哲学家毕生经营排了三六九等，确实不足。（当然教材的任务并不是要教导真理，否则没有完美的教材，而是探索真理的怀疑精神。教材有删改是天生的不足，但不对删改作反思则是后天的不足）人的思维何尝不爱如此，归类本是最后一步，这样却成了一种精致的偷懒。",-1),e("p",null,"润之说，马克思主义千头万绪，总结起来就一句话：造反有理。我想这是强调历史唯物主义，强调阶级斗争的一方面。造反，本质上就是遵循矛盾运动规律，推动矛盾运动的发展，怎么推动？就是实践。到中国的语境下，就是知行合一。马克思说，关键在于改造世界。从中国的传统上看，从古至今不缺改造世界的，或说经世致用的思想。私以为中国的哲学，处世是高于治学的，行有余力则以学文，绝不是为学而荒废了行。无论避世的或真或假，都是作为用世的对立而非其他。因而在改造世界这一观点上，古人都是执着的（当然这种改造，并非是进化论意义上的）。仅凭这点而言，我想相比他反对的那些纯粹的思想家，马克思会更加认同我们的古人。这也许是为什么，即便不那么“唯物”，中国思想天生能为东渐的马学留有一块余地。因为讲知行合一才是根本，连存在与思维的地位关系，也是为着这个最终的结论而服务的。",-1),e("p",null,"一点感悟，欢迎交流。",-1)]))}const u=a(l,[["render",s]]);export{m as __pageData,u as default};
