import{_ as a,c as s,g as t,o as n}from"./chunks/framework.CCxIPCIe.js";const _=JSON.parse('{"title":"一四","description":"","frontmatter":{"title":"一四","date":"2020-04-21T14:09:46.000Z","length":"243字","time":"1分钟","aside":false,"hash":-1621817601},"headers":[],"relativePath":"idea/14.md","filePath":"idea/14.md"}'),r={name:"idea/14.md"};function o(d,e,i,l,p,c){return n(),s("div",null,e[0]||(e[0]=[t("p",null,"初中每逢大考，宣传委员照例要在黑板上写艺术字。这以往都是一些“认真仔细”的话，并不新奇，顶多用个成语。偏这次院长很是振奋，在黑板上写下“燃烧吧，小宇宙！”几个叛逆的字来，引起一阵骚动。院长拍拍笔灰，志得意满地走下讲台。结果隔壁班的小未走过来，说这标语不伦不类，就给换掉了。那时大家都知道这是日漫的台词，被擦也无可厚非。",-1),t("p",null,"直到今天听王老师讲心学，讲到日本人对心学的推崇，弹幕里突然冒出一句“燃烧吧，小宇宙”，我才突然明白此话正是取自“吾心即是宇宙”。可是那天晚上，没人识出阳明。一群中二少年引这中二的话，原也自然。却不知那位同为惑者的“天姥游子”，如今得以放心了吗",-1)]))}const f=a(r,[["render",o]]);export{_ as __pageData,f as default};
