import{_ as a,c as l,g as e,o as n}from"./chunks/framework.n-91xA-b.js";const u=JSON.parse('{"title":"二二〇","description":"","frontmatter":{"title":"二二〇","date":"2022-08-11T00:02:24.000Z","length":"369字","time":"2分钟","aside":false,"hash":730030766},"headers":[],"relativePath":"idea/220.md","filePath":"idea/220.md"}'),s={name:"idea/220.md"};function r(o,t,d,i,p,c){return n(),l("div",null,t[0]||(t[0]=[e("p",null,"高中政治课讲货币的四种功能。关于辨析支付手段和流通手段，老师举了个去饭店吃饭的例子，说一手交钱一手交货就是流通手段，吃饭赊账就是支付手段。于是有学生问，吃饭我是先吃完饭再付钱，也不是一手交钱一手交货啊。老师说难道你吃一粒付一分钱吗？",-1),e("p",null,"当时始终觉得这个问题就这么被糊弄过去了。直到今天看到异步程序这个概念，忽然明白了。比如程序通过异步函数请求一个页面，它其实只是得到一个许诺，而这个许诺将在未来某个时刻得到兑现（或者拒绝）。结果就是请求程序并没有直接获得回复，通过许诺这个特殊变量，请求与回复被分离开来。",-1),e("p",null,"类似的在经济活动中，契约被发明出来，使得买和卖被分离开来。当货币行使它支付手段的功能时，其实已经和原来的商品没有关系了，买和卖通过契约解耦了。",-1),e("p",null,"因而判断货币到底是流通手段还是支付手段，其实就是判断是否有契约，买和卖是否解耦。",-1),e("p",null,"货币使得不同空间的人发生联系成为可能，而契约使得不同时间的人发生联系成为可能。",-1)]))}const _=a(s,[["render",r]]);export{u as __pageData,_ as default};
