import{_ as a,c as l,g as e,o as n}from"./chunks/framework.n-91xA-b.js";const m=JSON.parse('{"title":"二二三","description":"","frontmatter":{"title":"二二三","date":"2022-08-18T13:14:09.000Z","length":"494字","time":"2分钟","aside":false,"hash":1079656284},"headers":[],"relativePath":"idea/223.md","filePath":"idea/223.md"}'),s={name:"idea/223.md"};function r(o,t,p,d,i,c){return n(),l("div",null,t[0]||(t[0]=[e("p",null,"人常言：为学不在逼迫，而在于兴趣。言虽不谬，犹有未尽。问兴趣何以生，其本仍为诚意。意尚不诚，况于接物哉？必先诚意，然后明性即天理，会人乃至接物，感天理赋于万物，本无异于心。于是赞天地之化育，兴趣所以生。",-1),e("p",null,"然诚意最为难。心不同外物，眼不能观，耳不能闻，手不能触。然眼所以能观，耳所以能闻，手所以能触，皆出于心。是故不观不闻不触不能知心之存，故格物先于诚意。然唯观唯闻唯触亦不能尽心之性，故诚意亦不后于格物。于是格物诚意实乃一事，而人所知格物而疑诚意者，实好物而溺焉。腐儒好物，自拔于常人。常人懵懵懂懂，彼也溺于物，过犹不及耳。天理无往无向，岂可执乎？",-1),e("p",null,"观润之为学之初，专求大本大原，不问琐细尘物，此所谓立志。志，求于心不求于外者也。孔子十有五而志于学，比之今人就学，可谓老大。然就学并非志学，启蒙亦非心启。十有五而志学，其实早慧也。",-1),e("p",null,"志立而无往不利，如浩然之气不可胜用也。姑举润之文章为例，以窥一角：往于农则有湖南农民运动考察报告，往于商则有寻乌调查，往于兵则有星火燎原、论持久战，往于哲则有矛盾、实践二论，凡此种种不可胜举。故云君子不器，如水之近乎道，遇杯则杯遇盘则盘，无所不适。",-1),e("p",null,"孟子曰人皆可为尧舜，阳明曰贩夫走卒皆可为圣贤，润之诗云六亿神州尽尧舜。感圣人之道至大至光，古今心传一脉相承也。",-1),e("p",null,"赘说至此，无复加焉，其言不出四书。悔闻道不及，有阳明之叹。",-1)]))}const _=a(s,[["render",r]]);export{m as __pageData,_ as default};
