import{_ as a,c as l,g as e,o as n}from"./chunks/framework.n-91xA-b.js";const u=JSON.parse('{"title":"二二七","description":"","frontmatter":{"title":"二二七","date":"2022-09-04T08:35:42.000Z","length":"432字","time":"2分钟","aside":false,"hash":-795844318},"headers":[],"relativePath":"idea/227.md","filePath":"idea/227.md"}'),s={name:"idea/227.md"};function r(o,t,d,i,p,c){return n(),l("div",null,t[0]||(t[0]=[e("p",null,"当一个人否定人的社会性或者把人分为天性“动物性”与社会性两部分时，他便不可避免地推导出天性之不可改变，他便对人性中不可改变的恶无可奈何。",-1),e("p",null,"在这个预设结局下，一切试图改变人性的尝试都是徒劳的，因此他也不可避免的会陷入悲观主义。这种悲观不是别的，恰恰是他对人性的执着带来的。",-1),e("p",null,"马克思主义者认为，人的一切性质都是社会性，这个结论是解放全人类的前提。如果人性不能通过改变社会关系而得到改变，解放人类就是一句空话。可以说是否相信人的社会性是验证一个人是否具备共产主义信仰的必要条件。",-1),e("p",null,"因而有人认为马克思主义者过于乐观（或者说过于傲慢，认为人是可以改变的）。但马克思主义者相反能明白他的绝望情绪的起因，并试图把他从束缚他的生产关系中解放出来，从这个意义上说，马克思主义是悲天悯人的。值得再强调的是，无产阶级解放全人类是一个阶级斗争的历史过程，他的悲天悯人绝不是传统意义上的救世主或圣人以他的道德光辉启发或者救赎人，而是经过论证的历史过程。这就是所谓科学社会主义的科学的内涵。",-1),e("p",null,"昨晚和一个同学辩论，有感而发，反复修改总不尽意，姑妄观之。关键还是在于实践。",-1)]))}const _=a(s,[["render",r]]);export{u as __pageData,_ as default};
