import{_ as a,c as s,g as t,o as n}from"./chunks/framework.n-91xA-b.js";const _=JSON.parse('{"title":"二四二","description":"","frontmatter":{"title":"二四二","date":"2023-01-24T18:37:28.000Z","length":"318字","time":"2分钟","aside":false,"hash":-204409612},"headers":[],"relativePath":"idea/242.md","filePath":"idea/242.md"}'),r={name:"idea/242.md"};function o(d,e,i,l,p,c){return n(),s("div",null,e[0]||(e[0]=[t("p",null,"数学的困难往往不在于其概念的抽象，而在于其记号与概念的抽象层次不够协调。庸俗的教材总是不遗余力的铺陈记号，把不同层次的概念堆砌在一起，使学生迷失于命题的组成细节，无法专注于命题本身。于是乎对命题记号的记忆篡夺了命题本身，极大地增加了学生的心智负担。正如语言学习者执迷于语法、口音而忽视了语言最重要的交流功能。",-1),t("p",null,"根据程序设计的经验，复杂的底层细节不应在应用层面呈现，同样的人们至少应该使用两种形式的记号。在一般陈述时，在不造成混淆的前提下，使用最简形式；只有在证明时，才应当事无巨细的展开所需的一切细节。庸俗的数学写作往往混淆这两种使用场景，采取一种看似自然却只方便了它自己的“惯例”。借助程序设计的经验，任何记号都首次出现必须声明，冗长的证明细节应该被封装为引理，局部变量的作用域应当明确。",-1)]))}const f=a(r,[["render",o]]);export{_ as __pageData,f as default};
