import{_ as a,c as n,g as e,o as s}from"./chunks/framework.CCxIPCIe.js";const _=JSON.parse('{"title":"二四五","description":"","frontmatter":{"title":"二四五","date":"2023-03-17T07:09:08.000Z","length":"333字","time":"2分钟","aside":false,"hash":401820984},"headers":[],"relativePath":"idea/245.md","filePath":"idea/245.md"}'),l={name:"idea/245.md"};function r(o,t,d,i,p,c){return s(),n("div",null,t[0]||(t[0]=[e("p",null,"学习数学往往执着于微观却忽略了宏观。即执着于推导的正确性（证明），却轻视了动机（定理链条）。说得直白些，数学证明作为演绎逻辑，至多是充分必要。充分必要从微观上看就是同义反复。缺乏动机说的再多也是废话连篇。",-1),e("p",null,"这就好比稳态热力学系统微观上步步可逆，但是宏观上却从有序向无序演进。数学逻辑也必须在推理上保证正确的同时，从无序向有序演进。",-1),e("p",null,"所以说做任何事情，首先要确定目标，搞清楚大方向。微观上摸石头算不上错，但是宏观上不过河就有了是非对错。摸石头本身没错，但用摸石头来代替过河就是本末颠倒。",-1),e("p",null,"死记硬背定理证明，但求考试考个高分；上课照本宣科，力求混个职称就是实用主义。立志搞大问题，潜心钻研几十年；箪食瓢饮，饭馆打工也坚持不懈就是马克思主义。实用主义只追求微观上的正义性，所谓和光同尘、韬光养晦；马克思主义更追求宏观上的正义性，所谓粉骨碎身、九死未悔。",-1)]))}const f=a(l,[["render",r]]);export{_ as __pageData,f as default};
