import{_ as a,c as n,g as e,o as s}from"./chunks/framework.n-91xA-b.js";const _=JSON.parse('{"title":"三二二","description":"","frontmatter":{"title":"三二二","date":"2024-10-16T11:12:51.441Z","length":"586字","time":"2分钟","aside":false,"hash":-1959843965},"headers":[],"relativePath":"idea/322.md","filePath":"idea/322.md"}'),l={name:"idea/322.md"};function r(o,t,d,i,p,c){return s(),n("div",null,t[0]||(t[0]=[e("p",null,"人工智能对人类的存在的威胁，并非对其动物生命的威胁，而是对其人的存在的威胁。我们说人之所以为人，在于他对既定现实的超越性。超越性从每个主体的独特的劳动中流淌出来。主体的无可替代的劳动是他获得他者承认，从而注册进主体间网络的凭借。正是被主体间网络接纳，主体才主动的创建出属于他自己的生活，获得自己作为人的实在性与尊严。",-1),e("p",null,"人工智能剥夺的并不是他谋生的手段——人总可以从事最琐碎最微不足道最无新意的工作来维持自己生命的延续（而事实上这种工作能获得报酬，也绝不是因为工人不可替代，而根本是一种施舍，正如摇尾乞怜的狗也无条件的获得施舍，并非主人得到了什么。），但那样人就蜕化为动物；而是剥夺了他赖以成人的手段，原先工作的大部分环节，被证明是机械的，是可以被机器所取代的，剩余的细节被归并为极少数的人工。",-1),e("p",null,"因此人工智能造成了这样的结果：那些能驾驭人工智能的人才具备真正的智能，并把他们的智能赋予人工智能，此外的人被剥夺了智能，即作为人的存在方式的超越性。（又正因为人的超越性，即便那些无法驾驭人工智能的人，也会立马意识到自己的价值危机。因为在思维中，他已经能够超越性的想象自己的辛勤工作在人工智能面前会变得多么微不足道。因此，一方面他感到无比兴奋，因为一个强大的工具正等着他驾驭，这是他无比渴望的超越性——通过与它的结合超越过去的自己；另一方面他又感到无比焦虑，因为比起自己驾驭这种超越性，它被别人所驾驭用于超越自己的可能性更大。这是类似于魔戒般的诱惑与畏惧。）",-1),e("p",null,"这是我们不得不面对的现实。",-1)]))}const f=a(l,[["render",r]]);export{_ as __pageData,f as default};
