import{_ as a,c as s,g as e,o as n}from"./chunks/framework.CCxIPCIe.js";const _=JSON.parse('{"title":"三四九","description":"","frontmatter":{"title":"三四九","date":"2025-02-04T20:51:25.587Z","length":"298字","time":"1分钟","aside":false,"hash":-2130341987},"headers":[],"relativePath":"idea/349.md","filePath":"idea/349.md"}'),r={name:"idea/349.md"};function l(o,t,d,i,p,c){return n(),s("div",null,t[0]||(t[0]=[e("p",null,"妈妈拍了张照，说老爹给家里买了个柴火灶烤地瓜干，我不解，家里的烤箱不能用吗？老爹说买柴火灶是为了烧草木灰。我更不解，老爹说村里不让烧草木灰。买了柴火灶，就能烧火合法得到草木灰了。我笑，实在觉得这理由有些荒唐。",-1),e("p",null,"可转念一想，童年时不免遭到“玩火尿床”的禁忌，时常纳闷这火生水的规律如何能跳出五行之外？于是那时回老家最爱坐在灶台前，在做饭之余心安理得的欣赏炉膛中火苗跳跃，大概只有神奇的灶王爷能豁免我不被惩罚，毕竟我可是冒着尿床的风险孝顺爷爷奶奶。",-1),e("p",null,"我还一直纳闷，为什么人间要为小小的灶台封神？现在我明白了，因为灶神要负责豁免烧草木灰的我爹与玩火的我，乃至先祖燧人——往它的嘴里塞满文明的熟食，祈/乞求火神仁慈的燃烧。在禁忌的边缘试探文明，这样的神力怎能不值得膜拜？",-1)]))}const f=a(r,[["render",l]]);export{_ as __pageData,f as default};
