import{_ as t,c as a,g as s,o as r}from"./chunks/framework.n-91xA-b.js";const _=JSON.parse('{"title":"三五〇","description":"","frontmatter":{"title":"三五〇","date":"2025-03-28T21:40:44.995Z","length":"434字","time":"2分钟","aside":false,"hash":-778048058},"headers":[],"relativePath":"idea/350.md","filePath":"idea/350.md"}'),n={name:"idea/350.md"};function o(d,e,i,l,c,p){return r(),a("div",null,e[0]||(e[0]=[s("p",null,"对于没有病痛的青年人而言，生命每天都被毫无代价也毫无征兆的再生产出来。这似乎是一种福分，却同时是一笔债务，意识必须煞有介事的组织起今日的生活，否则它便无法心安理得的接受生命每日慷慨的馈赠。物质上，我们需要劳动来生产出维持存在所需的生活资料：意识上，只有生命以人的方式耗费，对应的生命意义才能被等额的生产出来。无论失去哪一方面，生命都将失去人的本质，即主体的否定性，否定它不甘于以动物生命自我延续的形式维持着。也就是说，作为人的生命而存在的意识，骨子里反对虚掷光阴，这就造成一个危机——每日我们都面临同样的问题，如何为今日的生命找到满意的消费路径？（这是否就是死本能？）消费生命就是把生命转化为死亡，并获得超越动物生命的意义。因而死亡本身也是苛刻的，它要求人必须以人的方式而死亡，以死亡实现人的生命的再生产。正因为如此，每天醒来只是动物生命完成了再生产，而每天的生活即如何死亡才是我们每天为了实现人的生命的再生产的任务。如果我们一日不死，便有一日累积起这存在的债务，它是实现了的人的生命与尚未实现的动物生命的差额，是庸庸碌碌，是生命力的通货膨胀。",-1)]))}const f=t(n,[["render",o]]);export{_ as __pageData,f as default};
