import{_ as a,c as s,g as t,o as n}from"./chunks/framework.CCxIPCIe.js";const _=JSON.parse('{"title":"三六〇","description":"","frontmatter":{"title":"三六〇","date":"2025-05-15T01:22:40.523Z","length":"529字","time":"2分钟","aside":false,"hash":-1957497690},"headers":[],"relativePath":"idea/360.md","filePath":"idea/360.md"}'),r={name:"idea/360.md"};function o(d,e,i,l,p,c){return n(),s("div",null,e[0]||(e[0]=[t("p",null,"受资本主义驯化的人们，耳濡目染地推崇资本的道德而排斥商品的道德，把资本与商品在市场中的关系照搬到人际关系：因为资本厌恶风险，于是它要把风险均摊，把自己投诸于不同对象以充分施展它买的权力，获得最大的回报。而商品则意味着被支配，被投为对象，因而它总要待价而沽，以稀缺性来抵御买的权力，或者说换取交易中更大的话语权，甚至倒转这种权力关系——也就是让商品比货币更为稀缺，从而反过来支配货币。于是在这种逻辑的驱使下，一个人作为买方要尽可能的追求买到所有东西，而作为卖方要尽可能的追求卖给不同的人——正因为它可以卖给所有人，因而对任意一个个别的买家而言，由于其他潜在买家的竞争，它是稀缺的。",-1),t("p",null,"但此时真正稀缺的是买断制。为了时刻保持买家竞争的可能性，商品不可能不卖，它只是变得不止卖一次，不只卖给一个人，因此买断制就变成了订阅制或者说租赁制。当市场中充满租赁制的商品以后，买方就要报复这种稀缺性，因为一方面它要在有限的租赁期内充分的榨取商品的使用价值，好似买断它一样，另一方面市场中到处都是的租赁制的商品使它不必担心榨干一件商品后再买不到其他商品。与买断的稀缺性对应的，一定是租赁的泛滥，而这种泛滥本身就在破坏租赁制的初衷——商品试图通过租赁制来规避买断制的风险变本加厉的回归了：买断是失去自由，租赁则是以自由的名义失去自由，即自由地失去自由，或失去自由的自由。",-1)]))}const f=a(r,[["render",o]]);export{_ as __pageData,f as default};
