import{_ as t,c as a,g as s,o as r}from"./chunks/framework.CCxIPCIe.js";const _=JSON.parse('{"title":"三八八","description":"","frontmatter":{"title":"三八八","date":"2025-12-26T13:45:41.266Z","length":"303字","time":"2分钟","aside":false,"hash":1505148539},"headers":[],"relativePath":"idea/388.md","filePath":"idea/388.md"}'),n={name:"idea/388.md"};function o(d,e,i,l,c,p){return r(),a("div",null,e[0]||(e[0]=[s("p",null,"比失业更可怕的是失去作为人的存在。的确，失业意味着失去生活资料，失去维系动物存在的物质基础，但有工作充其量只能获得动物存在，并不意味着就能获得作为人的存在。资本主义的糟糕之处不仅在于剥夺人的劳动，更在于它剥夺人的存在。资本为了让工人听命于自己，必须牢牢掌控生产资料，把生产拆分成细碎的环节，不仅便于分工，更便于分化工人的力量，避免某个个人占据关键的、无可取代的岗位，以此获得与资本谈判的话语权。因此每个人的劳动都越发微不足道，微末到无法凝聚起人的存在所需要的改造世界的意识，而只能把劳动的目的维系在同样无限可分的纯粹量（即工资）上。它无法真切的感受到自己的劳动对世界的贡献，只能通过言语来宣传，而这失去了反馈的言语，就会陷入一厢情愿的夸大，仿佛对着旷野嘶喊。",-1)]))}const f=t(n,[["render",o]]);export{_ as __pageData,f as default};
