import{_ as a,c as n,g as e,o as s}from"./chunks/framework.CCxIPCIe.js";const _=JSON.parse('{"title":"四四","description":"","frontmatter":{"title":"四四","date":"2020-09-21T20:43:24.000Z","length":"506字","time":"2分钟","aside":false,"hash":218993912},"headers":[],"relativePath":"idea/44.md","filePath":"idea/44.md"}'),l={name:"idea/44.md"};function r(o,t,d,i,p,c){return s(),n("div",null,t[0]||(t[0]=[e("p",null,"无意间翻到了学堂毕业生网站，看到了赵老师。整个页面只有他的照片是空白，很是刺眼。他的头衔也并非教授，只是个普通的讲师（这很文史哲）。尽管如此他对我的影响并不亚于数学老师。学数学非常耗费心力，此心力之恢复，正在赵老师的课堂上最为旺盛。因而他的课是我枯燥日子里翘首相盼的。我也永远不会忘记他那日在黑板上不知是巧合还是冥冥中的必然写下的润和心。在此之前，我心中确有这个理想的躯壳，但正是那一瞬，他把他的音容笑貌注进了这个理想，他让我意识到一颗近在咫尺的真心，富有激情活力。这个理想，终于不再是文章和细语，历史和风景，他它等来了另一颗此在此间的心，听它澎湃的跳动，一切都生机勃发起来，心中的郁结也冲荡一尽。此后虽也听过一些网络公开课，无论是山大的史记还是武大的说文亦或复旦的烟王，感情终不至此。如今又退回上网课，愈是感慨万分。",-1),e("p",null,"没看红楼梦以前，对它有公认的偏见，认为宝玉不过是个花花公子罢了。他总是发脾气，动不动就赌咒毒誓，还经常伤春悲秋泪流满面。殊不知他只想要一颗真心。谁都想要一颗真心，只是曹雪芹看穿了他无奈的幼稚戏。",-1),e("p",null,"这世界不乏真心，古书里到处是它的痕迹，但是活着跳动的真心，试问又能寻着几颗呢？",-1),e("p",null,"于是人们要么背叛了真心，要么沦为公认的幼稚病。真正找到的，是不是又秘而不宣，暗自窃喜呢？嗯这是羡慕，这是嫉妒，这是庸人妄加揣度 🥱。",-1)]))}const f=a(l,[["render",r]]);export{_ as __pageData,f as default};
